import React, { useFrame }from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';

// Reusable 3D Cube component
function DataPointCube({ position, color, value, onClick }) {
  const meshRef = React.useRef();
  const [hovered, setHover] = React.useState(false);
  const [active, setActive] = React.useState(false);

  // Animate rotation
  useFrame(() => {
    if (meshRef.current) {
      meshRef.current.rotation.y += 0.01;
    }
  });

  return (
    <mesh
      ref={meshRef}
      position={position}
      scale={active ? 1.5 : 1}
      onClick={() => {
        setActive(!active);
        if (onClick) onClick();
      }}
      onPointerOver={() => setHover(true)}
      onPointerOut={() => setHover(false)}
      castShadow
      receiveShadow
    >
      <boxGeometry args={[1, 1, 1]} />
      <meshStandardMaterial color={hovered ? 'hotpink' : color || 'orange'} />
    </mesh>
  );
}

// Main 3D Visualization Scene
const ThreeDVisualization = ({ data = [], onCubeClick }) => {
  return (
    <div style={{ width: '100%', height: '60vh' }}>
      <Canvas shadows camera={{ position: [0, 5, 10], fov: 60 }}>
        <ambientLight intensity={0.5} />
        <directionalLight position={[5, 10, 7.5]} intensity={1} castShadow />
        <OrbitControls />
        {/* Render cubes for each data point */}
        {data.map((point, idx) => (
          <DataPointCube
            key={point.id || idx}
            position={[point.x || idx * 2, point.y || 0, point.z || 0]}
            color={point.color || 'skyblue'}
            value={point.value}
            onClick={() => onCubeClick && onCubeClick(point)}
          />
        ))}
      </Canvas>
    </div>
  );
};

export default ThreeDVisualization;
