import React, { useState } from 'react';

const NetworkCard = ({ userInfo }) => {
    const [isSelected, setIsSelected] = useState(false);
    //console.log("User info in Network Card",userInfo);
    const handleCardClick = () => {
       setIsSelected(!isSelected);
    };

    return (
        <div  className="clickable" onClick={handleCardClick}>
            <div>|  +--------------------------------------+  |</div>
            <div> <span className={`selection-indicator ${isSelected ? 'selected-user' : ''}`}></span>  <span className="card-text">{userInfo.id}</span>  <span className="card-text">{userInfo.name}</span> <span className="card-text">{userInfo.username}</span> </div>
            <div>|  +--------------------------------------+  |</div>
            <div>|                                            |</div>
             <style>{`
                .checkbox-option{
                    margin-left: 2.0px;
                }

                .selected-border {
                    border: 2px solid blue;
                }

                .selection-indicator {
                    margin-right: 10px;
                    font-weight: bold;
                    color: #2196f3;
                }

                .selected {
                    background-color: #e3f2fd;
                    border: 2px solid #2196f3;
                }

                .selected-user{
                    border: 2px solid blue;
                    margin-left: 3px;
                    max-width: 352px;
                }
            `}</style>
        </div>

    );
};

export default NetworkCard;
