import React, { useState } from 'react';


const NetworkCard = ({ userInfo }) => {
    console.log("User info in Network Card",userInfo);
    const handleCardClick = () => {
        console.log("Clicked on friend in the network");
    };

    return (
        <div  className="clickable" onClick={handleCardClick}>
            <div>|  +--------------------------------------+  |</div>
            <div>     <span className="card-text">{userInfo.name}</span>  <span className="card-text">{userInfo.username}</span> </div>
            <div>|  +--------------------------------------+  |</div>
            <div>|                                            |</div>
             <style>{`
                .checkbox-option{
                    margin-left: 2.0px;
                }

            `}</style>
        </div>

    );
};

export default NetworkCard;
