        <Routes>
          <Route path="/" element={<LandingPage/>}/>
          <Route path="/landing" element={<LandingPage />} />
          <Route  path="/create-event" element={<CreateEvent/>}/>

        </Routes>
        <NavigationBar activeTab={activeTab} onSetActive={handleSetActive} />
      </Router>
    </div>
  );
}

export default App;
