import './App.css';
import React from 'react';
import {BrowserRouter as Router, Routes, Route} from "react-router-dom";
import CreateEvent from './components/CreateEvent';
import LandingPage from './components/LandingPage';
import Header from './components/ui/Header';
import NavigationBar from './components/ui/NavigationBar';

function App() {
  const styles = 
  `
    /* Combined CSS for Header and Navigation */

    /* General clickable elements */
    .clickable {
        cursor: pointer;
        color: blue;
        font-weight: bold;
    }

    a.clickable {
        color: blue;
    }

    /* Navigation items */
    .nav-item {
        cursor: pointer;
        margin-right: 5px;
    }

    .nav-item.active {
        font-weight: bold;
        color: #000;
    }

    .nav-item:hover {
        text-decoration: underline;
    }

    /* Create event link styling */
    .create-event-link {
        color: green;
        font-weight: bold;
    }

    /* Card and text styling */
    .card-text {
        color: #333;
    }

    /* Checkbox styling */
    .checkbox-option {
        display: inline;
        cursor: pointer;
    }

    .checkbox-option input[type="checkbox"] {
        margin-right: 5px;
        vertical-align: middle;
    }

    /* Form inputs and elements */
    .form-input, .form-textarea, .date-input, .time-input, input[type="checkbox"], label {
        background-color: transparent;
        border: none;
        outline: none;
        font-family: monospace;
        color: #333;
        vertical-align: middle;
    }

    /* Button styling */
    button {
        font-family: monospace;
        cursor: pointer;
        padding: 0px 4px;
        text-decoration: none;
        border: none;
        color: blue;
    }

    /* Guest selection positioning */
    .select_guest {
        margin-left: 300px;
    }

    .select_guests {
        position: absolute;
        margin-left: 90px;
    }

    /* ASCII container */
    .ascii-container {
        max-width: 600px;
        margin: 0 auto;
    }
    .ascii-container {
        max-width: 600px;
        margin: 0 auto;
        fontFamily: 'monospace', 
        backgroundColor: '#f0f0f0', 
        padding: '20px', 
        borderRadius: '8px', 
        whiteSpace: 'pre' 
    }
  `;


  const handleReturnHome= () => {
    console.log("Returning home")
  };

  const handleUserInfo = () => {
    console.log("user Info")
  };
  const activeTab= () => {
    console.log("user Info")
  };
  const handleSetActive= () => {
    console.log("user Info")
  };

  return (
    <div className="ascii-container" style={{ 
            fontFamily: 'monospace', 
            backgroundColor: '#f0f0f0', 
            padding: '20px', 
            whiteSpace: 'pre' 
        }}>
      <Router>
        <style>{styles}</style>
        <Header onReturnHome={handleReturnHome} onUserInfo={handleUserInfo} />
        <Routes>
          <Route path="/" element={<LandingPage/>}/>
          <Route path="/landing" element={<LandingPage />} />
          <Route  path="/create-event" element={<CreateEvent/>}/>

        </Routes>
        <NavigationBar activeTab={activeTab} onSetActive={handleSetActive} />
      </Router>
    </div>
  );
}

export default App;
