import React, {useEffect, useState } from 'react';

const MoreEventDetails = ({ event }) => {
    // --- Style Definitions ---
    const styles = {
        container: { fontFamily: 'sans-serif', backgroundColor: '#f9f9f9', border: '2px solid', width: '310px', borderRadius: '8px', padding: '16px 24px', margin: '16px 0' },
        header: { fontSize: '1.25rem', fontWeight: '600', color: '#333', borderBottom: '1px solid #e0e0e0', paddingBottom: '12px', marginBottom: '12px' },
        detailRow: { display: 'flex', alignItems: 'flex-start', marginBottom: '8px', fontSize: '0.9rem' },
        label: { fontWeight: 'bold', color: '#555', minWidth: '100px', flexShrink: 0 },
        value: { color: '#333' },
        descriptionContainer: { marginTop: '16px' },
        descriptionText: { fontSize: '0.9rem', lineHeight: '1.6', whiteSpace: 'pre-wrap', color: '#333' },
        participantsContainer: { marginTop: '16px' },
        participantItem: { padding: '4px 0', color: '#333' }
    };


    const [participants, setParticipants] = useState([]);

    useEffect(() => {
        const inviteesEndpoint = "http://localhost:3001/api/invitees";
        const networkEndpoint = "http://localhost:3001/api/load_network";
        const rsvpEndpoint = "http://localhost:3001/api/rsvp";


        // This single async function will fetch everything we need
        const fetchAndCombineData = async () => {
            try {
                // 1. Fetch both the invitee list and the full user network in parallel
                const [inviteesResponse, networkResponse, rsvpResponse] = await Promise.all([
                    fetch(inviteesEndpoint),
                    fetch(networkEndpoint),
                    fetch(rsvpEndpoint),
                ]);

                if (!inviteesResponse.ok || !networkResponse.ok || rsvpResponse) {
                    throw new Error('Failed to fetch data from one or more endpoints.');
                }

                const allGuests = await inviteesResponse.json();
                const network = await networkResponse.json();
                const rsvps = await rsvpResponse.json();

                console.log("all rsvps: ", rsvps);

                // 2. Filter for this event's specific participants
                const eventParticipants = allGuests.filter(guest => guest.event_id === event.id);
                console.log("Grouped event part: ", eventParticipants);

                // 3. Combine the data: Map participant IDs to names from the network
                const enrichedParticipants = eventParticipants.map(participant => {
                    // Find the full user object from the network list
                    const user = network.find(u => u.id === participant.invitee);

                    // Return a new object with the name included
                    return {
                        ...participant, // Keep original participant data (id, status, etc.)
                        name: user ? `${user.first_name} ${user.last_name}` : 'Unknown User'
                    };
                });



                // 4. Update the state with the new, combined data
                setParticipants(enrichedParticipants);

            } catch (error) {
                console.error("Error fetching and processing participant data:", error);
                setParticipants([]); // Reset on error
            }
        };

        // Only run the function if we have a valid event
        if (event && event.id) {
            fetchAndCombineData();
        }

    }, [event]);


    // Helper to format the date for better readability
    const formattedDate = event.event_date 
        ? new Date(event.event_date).toLocaleDateString('en-US', {
              weekday: 'long',
              year: 'numeric',
              month: 'long',
              day: 'numeric',
          })
        : 'Date not available';
    if (!event) {
        return <div>Loading event details...</div>;
    }

    return (
        <div style={styles.container}>
            <div style={styles.header}>{event.name || 'Event Details'}</div>

            <div style={styles.detailRow}>
                <span style={styles.label}>Date:</span>
                <span style={styles.value}>{formattedDate}</span>
            </div>

            <div style={styles.detailRow}>
                <span style={styles.label}>Time:</span>
                <span style={styles.value}>{event.time || 'Not specified'}</span>
            </div>

            <div style={styles.detailRow}>
                <span style={styles.label}>Location:</span>
                <span style={styles.value}>{event.location || 'Not specified'}</span>
            </div>

            <div style={styles.detailRow}>
                <span style={styles.label}>Event Type:</span>
                <span style={styles.value}>{event.type || 'General'}</span>
            </div>

            <div style={styles.descriptionContainer}>
                <div style={styles.label}>Description:</div>
                <p style={styles.descriptionText}>{event.description || 'No description provided.'}</p>
            </div>
            <div>{event.id}</div>

            <div style={styles.participantsContainer}>
                <div style={styles.label}>Participants:</div>
                {participants.length > 0 ? (
                    participants.map(p => (
                    <div key={p.id} style={styles.participantItem}>
                        {p.name} (Status: {p.status})
                    </div>
                    ))
                ) : (
                    <div style={{...styles.value, paddingTop: '4px'}}>No participants listed.</div>
                )}
            </div>
        </div>
    );
};

export default MoreEventDetails;
