import React from 'react';

const MoreEventDetails = ({ event }) => {
    // --- Style Definitions ---
    // We define all our styles here as an object.
    // This makes the JSX below clean and readable.
    const styles = {
        // The main container for the details section
        container: {
            fontFamily: 'sans-serif', // A clean, standard font
            backgroundColor: '#f9f9f9', // A very light grey, almost white
            //border: '1px solid #e0e0e0', // A soft, light-grey border
            border: '2px solid',
            width: '310px',
            borderRadius: '8px', // Slightly rounded corners
            padding: '16px 24px', // Vertical and horizontal spacing inside the card
            margin: '16px 0', // Space above and below the details block
        },
        // Style for the main title of the event
        header: {
            fontSize: '1.25rem',
            fontWeight: '600',
            color: '#333', // Dark grey for text, not pure black
            borderBottom: '1px solid #e0e0e0', // A line to separate title from details
            paddingBottom: '12px',
            marginBottom: '12px',
        },
        // A container for each row of details (e.g., Date, Time)
        detailRow: {
            display: 'flex',
            alignItems: 'flex-start',
            marginBottom: '8px', // Space between each detail row
            fontSize: '0.9rem',
        },
        // The label for each detail (e.g., "Location:")
        label: {
            fontWeight: 'bold',
            color: '#555', // A slightly lighter grey for labels
            minWidth: '100px', // Ensures all data fields align vertically
            flexShrink: 0,
        },
        // The actual data text
        value: {
            color: '#333',
        },
        // Specific container for the long-form description
        descriptionContainer: {
            marginTop: '16px',
        },
        // The description text itself
        descriptionText: {
            fontSize: '0.9rem',
            lineHeight: '1.6', // Improves readability for paragraphs
            whiteSpace: 'pre-wrap', // Respects line breaks from your database
            color: '#333',
        },
    };

    const createEventsBackendEndpoint = "http://localhost:3001/api/create_event";
    const inviteesEndpoint = "http://localhost:3001/api/invitees";

    const eventGuests = async () => {
        try {
            const response = await fetch(inviteesEndpoint);
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            const event_guests = await response.json();
            console.log(event_guests);
            return event_guests;
        } catch (error) {
            console.log("error loading event guests", error);
            return [];
        }
    };
    const loadEvents = async () => {
        try {
            const response = await fetch(createEventsBackendEndpoint);
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            const events = await response.json();
            console.log(events)
            return events;
        } catch (error) {
            console.log("error loading event events", error);
            return [];
        }
    };


    const mapParticipantsToEvents = (events, guests) => {
        return events.map(event => {
            const participants = guests.filter(guest => guest.event_id === event.id);
            return { ...event, participants };
        });
    };

    const event_guests = eventGuests();
    const events = loadEvents

    const eventsWithParticipants = mapParticipantsToEvents(loadEvents, eventGuests);
    console.log(JSON.stringify(eventsWithParticipants, null, 2));


    // Helper to format the date for better readability
    const formattedDate = event.event_date 
        ? new Date(event.event_date).toLocaleDateString('en-US', {
              weekday: 'long',
              year: 'numeric',
              month: 'long',
              day: 'numeric',
          })
        : 'Date not available';

    return (
        <div style={styles.container}>
            <div style={styles.header}>{event.name || 'Event Details'}</div>

            <div style={styles.detailRow}>
                <span style={styles.label}>Date:</span>
                <span style={styles.value}>{formattedDate}</span>
            </div>

            <div style={styles.detailRow}>
                <span style={styles.label}>Time:</span>
                <span style={styles.value}>{event.time || 'Not specified'}</span>
            </div>

            <div style={styles.detailRow}>
                <span style={styles.label}>Location:</span>
                <span style={styles.value}>{event.location || 'Not specified'}</span>
            </div>

            <div style={styles.detailRow}>
                <span style={styles.label}>Event Type:</span>
                <span style={styles.value}>{event.type || 'General'}</span>
            </div>

            <div style={styles.descriptionContainer}>
                <div style={styles.label}>Description:</div>
                <p style={styles.descriptionText}>{event.description || 'No description provided.'}</p>
            </div>
        </div>
    );
};

export default MoreEventDetails;
