import { useState, useEffect } from "react";
import NavigationBar from "./ui/NavigationBar";
import SendEvitesHeader from "./ui/SendEvitesHeader";
import NetworkCard from "./ui/NetworkCard";
export default function SendEvites() {

    const loadNetworkApi = "http://localhost:3001/api/load_network";
    const [users, setUsers] = useState([]);
    const [activeTab, setActiveTab] = useState('HOME');

    const handleReturnHome = () => {
        console.log("Returning to home");
        setActiveTab('HOME');
    };

    const handleUserInfo = () => {
        console.log("Show user info");
    };

    const loadNetwork = async () => {
        try{
            const response = await fetch(loadNetworkApi);
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            const network = await response.json();
            //console.log("This is your network:", network);

            const processedUsers = network.map(user => ({
                id: user.id,
                name: user.first_name + " " + user.last_name,
                username: user.username 
            }));
            setUsers(processedUsers);
        }catch (error){
            console.log("erro loading network", error);
        }

    };

    // --- Effects ---
    useEffect(() => {
        loadNetwork();
    }, []);

    //console.log("After processing users: ", users);
    const all_users = users;
    return(
        <div className="ascii-container" style={{ 
            fontFamily: 'monospace', 
            backgroundColor: '#f0f0f0', 
            padding: '20px', 
            borderRadius: '8px', 
            whiteSpace: 'pre' 
        }}>
        <SendEvitesHeader onReturnHome={handleReturnHome} onUserInfo={handleUserInfo} />
