import React, { useState } from 'react';

const NetworkCard = ({ userInfo }) => {
    const [isSelected, setIsSelected] = useState(false);
    //console.log("User info in Network Card",userInfo);
    const handleCardClick = () => {
       setIsSelected(!isSelected);
    };

    return (
        <div  className="clickable" onClick={handleCardClick}>
            <div>|  +--------------------------------------+  |</div>
            {isSelected}
            <div>   <span className="selection-indicator">
                    {isSelected ? '✓' : '○'}
                </span>  
                   <span className="card-text">{userInfo.id}</span>  
                   <span className="card-text">{userInfo.name}</span>
                   <span className="card-text">{userInfo.username}</span> </div>
            <div>|  +--------------------------------------+  |</div>
            <div>|                                            |</div>
             <style>{`
                .checkbox-option{
                    margin-left: 2.0px;
                }

            `}</style>
        </div>

    );
};

export default NetworkCard;
