import React, { useState, useEffect } from 'react';

const NetworkCard = ({ userInfo }) => {
    const [isSelected, setIsSelected] = useState(false);
    const [selectedUser, setSelectedUser] = useState(null);

    //console.log("User info in Network Card",userInfo);
    const handleCardClick = () => {
       setIsSelected(!isSelected);
    };


    function storeSeletedUser(user){
        setSelectedUser(user);
    }

    useEffect(() => {
        if(selectedUser){
            console.log("Inside the useEffect", selectedUser)
        }
    },[selectedUser]);

    return (
        <div  className="clickable" onClick={handleCardClick}>
            <div>|  +--------------------------------------+  |</div>
           <div className={`user-info ${isSelected ? 'selected-user' : ''}`}> <span className="selection-indicator"> {isSelected ?  storeSeletedUser(userInfo.id) : '○'} </span>   <span className={`selection-indicator ${isSelected ? 'selected-user' : ''}`}></span> <span className="card-text">{userInfo.name}</span> <span className="card-text">{userInfo.username}</span> </div>
            <div>|  +--------------------------------------+  |</div>
            <div>|                                            |</div>
             <style>{`
                .checkbox-option{
                    margin-left: 2.0px;
                }

                .selected-user {
                    border: 2px solid blue;
                    margin-left: 25px;
                    max-width: 308px;
                    height: 20px;
                }

                .user-info {
                    display: flex;
                    align-items: center;
                }

                .selection-indicator {
                    margin-right: 10px;
                    margin-left: 20px;
                    font-weight: bold;
                    color: #2196f3;

                }

                .card-text {
                    margin-right: 15px;
                }
            `}</style>
        </div>

    );
};

export default NetworkCard;
