const MoreEventDetails = ({event}) => {
    const formattedDate = new Date(event.date).toLocaleDateString('en-US', {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
    });
    return(
        <>
            <div>|           Event Details                    |</div>
            <div>|                                            |</div>
            <div>|  {event.name}                      |</div>
            <div>|  Date: {formattedDate}                     |</div>
            <div>|  Time: {event.time}                  |</div>
            <div>|  Location: {event.location}                |</div>
            <div>|  Event Type: {event.event_type}            |</div>
            <div>|  +--------------------------------------+  |</div>
            <div>|  Description:                              |</div>
            <div style={{ padding: '0 40px', whiteSpace: 'pre-wrap' }}>{event.description}</div>
            <div>|  +--------------------------------------+  |</div>
            <div>|                                            |</div>
        </>
    )
};

export default MoreEventDetails;
