import React, { useState } from 'react';

const NetworkCard = ({ userInfo }) => {
    const [isSelected, setIsSelected] = useState(false);
    //console.log("User info in Network Card",userInfo);
    const handleCardClick = () => {
       setIsSelected(!isSelected);
    };

    console.log(isSelected, userInfo.id);

    return (
        <div  className="clickable" onClick={handleCardClick}>
            <div>|  +--------------------------------------+  |</div>
           <div className={`user-info ${isSelected ? 'selected-user' : ''}`}> <span className="selection-indicator"> {isSelected ? userInfo.id : '○'} </span>   <span className={`selection-indicator ${isSelected ? 'selected-user' : ''}`}></span> <span className="card-text">{userInfo.name}</span> <span className="card-text">{userInfo.username}</span> </div>
            <div>|  +--------------------------------------+  |</div>
            <div>|                                            |</div>
             <style>{`
                .checkbox-option{
                    margin-left: 2.0px;
                }

                .selected-user {
                    border: 2px solid blue;
                    margin-left: 25px;
                    max-width: 308px;
                    height: 20px;
                }

                .user-info {
                    display: flex;
                    align-items: center;
                }

                .selection-indicator {
                    margin-right: 10px;
                    margin-left: 20px;
                    font-weight: bold;
                    color: #2196f3;

                }

                .card-text {
                    margin-right: 15px;
                }
            `}</style>
        </div>

    );
};

export default NetworkCard;
