import { useState, useEffect } from "react";
import NavigationBar from "./ui/NavigationBar";
import SendEvitesHeader from "./ui/SendEvitesHeader";
export default function SendEvites() {

    const loadNetworkApi = "http://localhost:3001/api/load_network";
    const [users, setUsers] = useState([]);
    const loadNetwork = async () => {
        try{
            const response = await fetch(loadNetworkApi);
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            const network = await response.json();
            console.log("This is your network:", network);

            const processedUsers = network.map(user => ({
                id: user.id,
                name: user.first_name + " " + user.last_name,
                username: user.username 
            }));
            setUsers(processedUsers);
        }catch (error){
            console.log("erro loading network", error);
        }

    };

    // --- Effects ---
    useEffect(() => {
        loadNetwork();
    }, []);


    return(
        <> 
        <SendEvitesHeader/>;


        <NavigationBar/>;
        <style>{`
                .clickable { cursor: pointer; text-decoration: underline; color: blue; }
                a.clickable { color: blue; }
                .form-input, .form-textarea, .date-input, .time-input, input[type="checkbox"], label {
                    background-color: transparent;
                    border: none;
                    outline: none;
                    font-family: monospace;
                    color: #333;
                    vertical-align: middle;
                }
                button {
                    font-family: monospace;
                    cursor: pointer;
                    background-color: #ddd;
                    border: 1px solid #000;
                    padding: 2px 4px;
                }
                .nav-item { cursor: pointer; }
                .nav-item.active { font-weight: bold; }
            `}</style>
        </>
    )

}
