import React, { useState } from 'react';
import MoreEventDetails from './MoreEventDetails';

const EventCard = ({ event, isPrivate = false, onRSVP }) => {
    const [willAttend, setWillAttend] = useState(false);
    const [willNotAttend, setWillNotAttend] = useState(false);
    const [isExpanded, setIsExpanded] = useState(false);

    const handleWillAttendChange = (checked) => {
        setWillAttend(checked);
        if (checked) setWillNotAttend(false);
        onRSVP && onRSVP(event.id, checked ? 'yes' : null);
    };

    const handleWillNotAttendChange = (checked) => {
        setWillNotAttend(checked);
        if (checked) setWillAttend(false);
        onRSVP && onRSVP(event.id, checked ? 'no' : null);
    };

    const handleCardClick = () => {
        setIsExpanded(!isExpanded);
    };

    const padText = (text, length) => {
        if (text.length > length) {
            return text.substring(0, length - 3) + '...';
        }
        return text.padEnd(length, ' ');
    };

    return (
        <div  className="clickable" onClick={handleCardClick}>
            <div>|  +--------------------------------------+  |</div>
            <div>|  |     <span className="card-text">padText(`Location: ${event.location}`, 30)</span>   |  |</div>
            <div>|  |     <span className="card-text">{event.location}</span>   |  |</div>
            <div>|  |     <span className="card-text">{event.date}</span>   |  |</div>
            <div>|  |     <span className="card-text">{event.time}</span>   |  |</div>
            <div>|  |     <span className="card-text">{`Description: ${event.description.substring(0,)}...`}</span>   |  |</div>            
            {isExpanded && <MoreEventDetails event={event} />}
            {isPrivate && (
                <>
                    <div>|  |        <span className="card-text"> [  RSVP ]</span>                    |  |</div>
                    <div>|  |   <label className="checkbox-option">  <input type="checkbox" className="box" checked={willAttend} onChange={(e) => handleWillAttendChange(e.target.checked)} /> <span className="card-text">Yes I will be attending</span></label>      |  |</div>
                    <div>|  |   <label className="checkbox-option">  <input type="checkbox" className="box" checked={willNotAttend} onChange={(e) => handleWillNotAttendChange(e.target.checked)} /> <span className="card-text"> will not be attending </span></label>      |  |</div>
                </>
            )}
            <div>|  +--------------------------------------+  |</div>
            <div>|                                            |</div>
             <style>{`
                .checkbox-option{
                    margin-left: 2.0px;
                }

            `}</style>
        </div>

    );
};

export default EventCard;
