import React, { useState, useEffect } from 'react';
import LandingPageHeader from './ui/LandingPageHeader';
import NavigationBar from './ui/NavigationBar';
import WelcomeSection from './ui/WelcomeSection';
import EventCard from './ui/EventCard';
import usePageNavigation from './ui/usePageNavigation';



// Main Landing Page component
export default function LandingPage() {
    // --- State Management ---
    const [events, setEvents] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [activeTab, setActiveTab] = useState('HOME');
    const [userName] = useState('Sarah');
    const navigate = usePageNavigation();


    // --- API Endpoints ---
    //const backendEndpoint = "http://localhost:3001/api/events";
    const createEventsBackendEndpoint = "http://localhost:3001/api/create_event";
    //const aiPost = "http://localhost:3001/api/ai_output";
    //const aiInput = "http://localhost:3001/api/ai_input";

    // --- Utility Functions ---


    const formatEventDate = (eventTime) => {
        const date = new Date(eventTime);
        return date.toLocaleDateString('en-US', {
            weekday: 'long',
            month: 'short',
            day: 'numeric',
            hour: 'numeric',
            minute: '2-digit'
        });
    };

    // --- Event Handlers ---
    const handleReturnHome = () => {
        console.log("Returning to home");
        setActiveTab('HOME');
    };

    const handleUserInfo = () => {
        console.log("Show user info");
    };

    const handleCreateEvent = () => {
        navigate('../CreateEvent');
    };

    const handleSetActive = (navItem) => {
        setActiveTab(navItem);
        console.log(`Navigating to: ${navItem}`);
    };

    async function handleRSVP(eventId, response){
        console.log(`RSVP for event ${eventId}: ${response}`);
        const rsvpEndpoint = "http://localhost:3001/api/rsvp";
        // Send RSVP to the backend with, userId(Who RSVP'ed), eventId (Event they will or not go to)
        const rsvpData = {
            userId: 1, 
            eventId: eventId,
            status: response, 
        }
        console.log("rsvp data: ", rsvpData);
        try{
            const response =  await fetch(rsvpEndpoint, {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(rsvpData),
            });

            if(!response.ok){
                console.error("Error with rsvp Status:", response.status);
                    alert(`Error: Could not rsvp. Server responded with status ${response.status}.`);
            } else {
                const data = await response.json();
                console.log("Heard back from rsvp table:", data);
                alert("Event created successfully!");
            }
        } catch (error){
            console.error("Failed to send request:", error);
            alert(`Error: Failed to send request.`);
        }

    };

    // --- Load Events ---
    const loadEvents = async () => {
        try {
            setLoading(true);
            const response = await fetch(createEventsBackendEndpoint);
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            const eventsData = await response.json();

            //console.log("Id test: ",eventsData);

            const processedEvents = eventsData.map(event => ({
                id: event.id,
                name: event.title, // Use the raw title
                location: event.location, // Use the raw location
                event_date: event.event_date, // Pass the full date object for formatting later
                time: event.event_time, // Use the raw time
                description: event.description, // Use the FULL, untruncated description
                type: event.event_type
            }))

            setEvents(processedEvents);
        } catch (error) {
            console.error("Error fetching events:", error);
            setError("Error loading events");
            // Set fallback data for demo
            setEvents([
                {
                    id: 1,
                    name: "Sample Private Event", 
                    location: "Location: Sample Location",
                    date: "Monday, Jan 15, 2:00 PM",
                    description: "Description: Sample event",
                    type: 'private'
                },
                {
                    id: 2,
                    name: "Sample Public Event",
                    location: "Location: Public Venue",
                    date:"Tuesday, Jan 16, 3:00 PM",
                    description:"Description: Public event",
                    type: 'public'
                }
            ]);
        } finally {
            setLoading(false);
        }
    };

    // --- Effects ---
    useEffect(() => {
        loadEvents();
    }, []);

    // --- Filter Events ---
    const privateEvents = events.filter(event => event.type === 'private');

    const publicEvents = events.filter(event => event.type === 'public');

    // --- Component Rendering ---
    return (
        <div className="ascii-container" style={{ 
            fontFamily: 'monospace', 
            backgroundColor: '#f0f0f0', 
            padding: '20px', 
            borderRadius: '8px', 
            whiteSpace: 'pre' 
        }}>
            <LandingPageHeader onReturnHome={handleReturnHome} onUserInfo={handleUserInfo} />

            <WelcomeSection userName={userName} onCreateEvent={handleCreateEvent} />

            {loading ? (
                <div>|           Loading events...                |</div>
            ) : error ? (
                <div>|           {error}                          |</div>
            ) : (
                <>
                    {/* Exclusive E-vites Section */}
                    <div>|           [ Exclusive E-vites ]            |</div>
                    <div>|                                            |</div>

                    {privateEvents.length > 0 ? (
                        privateEvents.map(event => (
                            <EventCard 
                                key={`private-${event.id}`} 
                                event={event} 
                                isPrivate={true}
                                onRSVP={handleRSVP}
                            />
                        ))
                    ) : (
                       <>
                            <div>|  +--------------------------------------+  |</div>
                            <div>|  |     No private events available       |  |</div>
                            <div>|  +--------------------------------------+  |</div>
                            <div>|                                            |</div>
                        </> 
                    )}

                    <div>|--------------------------------------------|</div>

                    {/* Public E-vites Section */}
                    <div>|            <span className="clickable">[ Public E-vites ]</span>  |</div>

                    {publicEvents.length > 0 ? (
                        publicEvents.map(event => (
                            <EventCard 
                                key={`public-${event.id}`} 
                                event={event} 
                                isPrivate={false}
                            />
                        ))
                    ) : (
                        <>
                            <div>|  +--------------------------------------+  |</div>
                            <div>|  |     No public events available        |  |</div>
                            <div>|  +--------------------------------------+  |</div>
                            <div>|                                            |</div>
                        </>
                    )}
                </>
            )}

            <NavigationBar activeTab={activeTab} onSetActive={handleSetActive} />

            <style>{`
                .clickable { 
                    cursor: pointer;
                    color: blue; 
                }
                .create-event-link {
                    color: green;
                    font-weight: bold;
                }
                .card-text {
                    color: #333;
                }
                .checkbox-option {
                    display: inline;
                    cursor: pointer;
                }
                .checkbox-option input[type="checkbox"] {
                    margin-right: 5px;
                    vertical-align: middle;
                }
                .nav-item { 
                    cursor: pointer; 
                    margin-right: 5px;
                }
                .nav-item.active { 
                    font-weight: bold; 
                    color: #000;
                }
                .nav-item:hover {
                    text-decoration: underline;
                }
                .ascii-container {
                    max-width: 600px;
                    margin: 0 auto;
                }
            `}</style>
        </div>
    );
}
