import React from 'react';

const NetworkCard = ({ userInfo, isSelected, onCardClick }) => {
    return (
        <div  className="clickable" onClick={() => onCardClick(userInfo)}>
            <div>|  +--------------------------------------+  |</div>
           <div  className={`user-info ${isSelected ? 'selected-user' : ''}`}>| <span className="selection-indicator">|{isSelected ? userInfo.id : '○'} </span>   <span className={`selection-indicator ${isSelected ? 'selected-user' : ''}`}></span> <span className="card-text">{userInfo.name}</span> <span className="card-text">{userInfo.username}</span> </div>
            <div>|  +--------------------------------------+  |</div>
            <div>|                                            |</div>
             <style>{`
                .checkbox-option{
                    margin-left: 2.0px;
                }

                .selected-user {
                    max-width: 308px;
                    height: 20px;
                }

                .user-info {
                    display: flex;
                    align-items: center;
                }

                .selection-indicator {
                    margin-right: 10px;

                    font-weight: bold;


                }


            `}</style>
        </div>

    );
};

export default NetworkCard;
